etherlike_mib_dot3PauseEntry_t *
etherlike_mib_new_dot3PauseEntry()
{
    etherlike_mib_dot3PauseEntry_t *dot3PauseEntry;

    dot3PauseEntry = (etherlike_mib_dot3PauseEntry_t *) g_malloc0(sizeof(etherlike_mib_dot3PauseEntry_t) + sizeof(gpointer));
    return dot3PauseEntry;
}

static int
unpack_dot3PauseEntry(GSnmpVarBind *vb, etherlike_mib_dot3PauseEntry_t *dot3PauseEntry)
{
    int idx = 11;

    if (vb->id_len < idx) return -1;
    dot3PauseEntry->dot3StatsIndex = vb->id[idx++];
    if (vb->id_len > idx) return -1;
    return 0;
}

static int
pack_dot3PauseEntry(guint32 *base, gint32 dot3StatsIndex)
{
    int idx = 11;

    base[idx++] = dot3StatsIndex;
    return idx;
}

static etherlike_mib_dot3PauseEntry_t *
assign_dot3PauseEntry(GSList *vbl)
{
    GSList *elem;
    etherlike_mib_dot3PauseEntry_t *dot3PauseEntry;
    guint32 idx;
    char *p;

    dot3PauseEntry = etherlike_mib_new_dot3PauseEntry();
    if (! dot3PauseEntry) {
        return NULL;
    }

    p = (char *) dot3PauseEntry + sizeof(etherlike_mib_dot3PauseEntry_t);
    * (GSList **) p = vbl;

    if (unpack_dot3PauseEntry((GSnmpVarBind *) vbl->data, dot3PauseEntry) < 0) {
        g_warning("illegal dot3PauseEntry instance identifier");
        g_free(dot3PauseEntry);
        return NULL;
    }

    for (elem = vbl; elem; elem = g_slist_next(elem)) {
        GSnmpVarBind *vb = (GSnmpVarBind *) elem->data;

        if (lookup(vb, oid_dot3PauseEntry, sizeof(oid_dot3PauseEntry)/sizeof(guint32),
                   attr_dot3PauseEntry, &idx) < 0) continue;

        switch (idx) {
        case 1:
            dot3PauseEntry->dot3PauseAdminMode = &(vb->syntax.i32[0]);
            break;
        case 2:
            dot3PauseEntry->dot3PauseOperMode = &(vb->syntax.i32[0]);
            break;
        case 3:
            dot3PauseEntry->dot3InPauseFrames = &(vb->syntax.ui32[0]);
            break;
        case 4:
            dot3PauseEntry->dot3OutPauseFrames = &(vb->syntax.ui32[0]);
            break;
        };
    }

    return dot3PauseEntry;
}

void
etherlike_mib_get_dot3PauseTable(GSnmpSession *s, etherlike_mib_dot3PauseEntry_t ***dot3PauseEntry, gint mask)
{
    GSList *in = NULL, *out = NULL;
    GSList *row;
    int i;
    static guint32 base[] = {1, 3, 6, 1, 2, 1, 10, 7, 10, 1, 0};

    *dot3PauseEntry = NULL;

    add_attributes(s, &in, base, 11, 10, attr_dot3PauseEntry, mask);

    out = gsnmp_gettable(s, in);
    /* g_snmp_vbl_free(in); */

    if (out) {
        *dot3PauseEntry = (etherlike_mib_dot3PauseEntry_t **) g_malloc0((g_slist_length(out) + 1) * sizeof(etherlike_mib_dot3PauseEntry_t *));
        if (! *dot3PauseEntry) {
            s->error_status = G_SNMP_ERR_INTERNAL;
            g_snmp_vbl_free(out);
            return;
        }
        for (row = out, i = 0; row; row = g_slist_next(row), i++) {
            (*dot3PauseEntry)[i] = assign_dot3PauseEntry(row->data);
        }
    }
}

